import React, {useState, useEffect} from 'react'

const Register = () => {

    const [inputValue, setinputValue] = useState({
        firstname: '',
        lastname: '',
        email: '',
        password: '',
        confirmpassword: '', 
    })
    const [allentry, setallentry] = useState([])
    const [errorentry, setallerror] = useState({})
    const [isRegister, setisRegister] = useState(false)


    const handleSubmit = (e)=>{
        e.preventDefault();
        
        const entries = {
            firstname: inputValue.firstname,
            lastname: inputValue.lastname,
            email: inputValue.email,
            password: inputValue.password,
            confirmpassword: inputValue.confirmpassword, 
        }

        setallentry([...allentry, entries])
        setallerror(validate(inputValue))
        setisRegister(true)
    }


    useEffect(() =>{
        if(Object.keys(errorentry).length === 0 && isRegister){
            console.log(inputValue);
        }
    }, [errorentry])

    const handleChange = (event) =>{
        const {name, value} = event.target
        setinputValue({...inputValue, [name]: value})        
    }

    const validate = (e) => {
        // const error = {}

        if(e.password === e.confirmpassword){
            console.log("matched");
        }
        else{
            console.log('not matched')
        }
    }


  return (
    <>
        <form action="" onSubmit={handleSubmit}>
            <div>
                <label htmlFor="FirstName">
                    First Name
                </label>
                <input type="name" name='firstname' 
                 value={inputValue.firstname}
                 onChange = {(handleChange)}
                 />
            </div>
            <div>
                <label htmlFor="FirstName">
                    lastt Name
                </label>
                <input type="name" name='lastname' 
                 value={inputValue.lastname}
                 onChange = {handleChange}
                 />
            </div>
            <div>
                <label htmlFor="email">
                    E-mail Address
                </label>
                <input type="email" name='email' autoComplete='off' 
                 value={inputValue.email}
                 onChange = {handleChange}
                 />
            </div>
            <div>
                <label htmlFor="password">
                    Password
                </label>
                <input type="password" name='password' 
                 value={inputValue.password}
                 onChange = {handleChange}
                 />
            </div>
            <div>
                <label htmlFor="confirmpassword">
                    Confirm Password
                </label>
                <input type="password" name='confirmpassword' 
                 value={inputValue.confirmpassword}
                 onChange = {handleChange}
                 />
            </div>
            <div>
                <button type='submit'>Register</button>
            </div>
        </form>
    </>
  )
}

export default Register;
