import React, {useState} from "react";
import { useEffect } from "react";
import logo  from "./bg.jpg"
import './index.css'

const Register = () => {
  const [inputValue, setinputValue] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    confirmpassword: "",
  });
  // const [allentry, setallentry] = useState([]);
  
  
  // const initialvalues = {
  //   firstname: "",
  //   lastname: "",
  //   email: "",
  //   password: "",
  //   confirmpassword: ""
  // }
  const [inputArr, setinputArr] = useState([])
  const [errors, seterrors] = useState({})
  const [isSubmit, setisSubmit] = useState(false)
  const [allentry, setallentry] = useState([]);
  
  
  //using backend
  const PostData = async () => {
    // e.preventDefault();
    const { firstname, lastname, email, password, confirmpassword } =
      inputValue;

    const res = await fetch("/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        firstname,
        lastname,
        email,
        password,
        confirmpassword
      }),
    });
    const response = await res.json();
    if (response.status === 422 || !response) {
      console.log("invalid registration");
    } else {
      console.log("Successfully registered");
    }
  };
  
  const handleChange = (event) => {
    const { name, value } = event.target;
    setinputValue({ ...inputValue, [name]: value });
  };
  

  const { 
    firstname, 
    lastname, 
    email, 
    password,
    confirmpassword }=inputValue;

  const handleSubmit = (e) => {
    e.preventDefault();
    // const { firstname, lastname, email, password, confirmpassword } =
    //   inputValue;
    
    setinputArr([...inputValue, [firstname, lastname, email, password, confirmpassword]])
    console.log(inputValue)
    console.log(inputArr)
    // setinputValue("")    
    setisSubmit(true) 
    //   if(password===confirmpassword){
    //     console.log('matched');
        
    //     setinputValue({
    //       firstname: "",
    //       lastname: "",
    //       email: "",
    //       password: "",
    //       confirmpassword:""
    //     })
    //   }
    //   else{
    //     console.log('not matched'); 
    //     setinputValue({
    //       password: "",
    //       confirmpassword:""
    //     })
    //   }
    // }
            
  };

  
  useEffect(()=>{
    console.log(errors);
    if(Object.keys(errors).length === 0 && isSubmit){
      console.log(inputValue);
    }
  }, [errors])
  
  const userExists = (val)=>{
   for (let index = 0; index < allentry.length; index++) {
    const elem = allentry[index].email;
    if(elem === val){
      console.log('email existed');
    }
    else{
    const entries = {
      firstname: inputValue.firstname,
      lastname: inputValue.lastname,
      email: inputValue.email,
      password: inputValue.password,
      confirmpassword: inputValue.confirmpassword
    };
      setallentry([...allentry, entries]);
    }
   }
  }
  
  const setValue = (e) =>{
    
    // if(userExists(e)){
    //   console.log('null');
    // }
    // else{
      const entries = {
        firstname:inputValue.firstname,
        lastname:inputValue.lastname,
        email:inputValue.email,
        password:inputValue.password,
        confirmpassword:inputValue.confirmpassword,
      };    
      setallentry([...allentry, entries]);
    // }
    
      
      
      
      
    }
    // const arrExist = ()=>{    
    //   inputValue.length === allentry.length && inputValue.every((el)=>{
    //     if(allentry.indexOf(el) > -1){
    //       return (el= allentry[allentry.indexOf(el)] )
    //     }
    //     else{
    //       console.log('not same');
    //     }
    //   })
    // }
  
  const validate = (values) =>{
    const errors = {}
    // const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i
    
    if(!values.firstname){
      errors.firstname="Firstname is required!";
    }
    if(!values.lastname){
      errors.lastname="Lastname is required!";
    }
    if(!values.email){
      errors.email="Email-address is required!";}
    // }else if(!regex.test(values.email)){
    //   errors.email="This is'nt a valid email address"
    // }
    if(!values.password){
      errors.password="Password is required!";
    }
    if(values.password!==values.confirmpassword){
      errors.password="Password does not match";
      errors.confirmpassword="Password does not match";
    }
    if(userExists(values.email)){
      errors.email='already existed, try anotherone'
    }
    if(!values.confirmpassword){
      errors.confirmpassword="Password is required!";
    }
    
    
    
    return errors;
  }

 

  return (
    <>
    <section className="flex flex-col md:flex-row h-screen items-center">
      <div className="bg-blue-600 hidden lg:block md:w-1/2 xl:w-2/3  h-screen">
        <img src={logo} alt="" className="w-screen h-screen object-cover" />
      </div>

      <div className="backdrop-blur-sm md:backdrop-blur-lg bg-white flex justify-center items-center  md:mx-auto md:max-w-sm lg:max-w-full w-full md:w-1/2 xl:w-1/3 px-6 lg:px-16 xl:px-12">
        <div className="w-screen h-screen pt-8">
          <h1 className="text-3xl font-black uppercase font-mono pb-2">phoneshop</h1>
          <h1 className="text-xl font-bold leading-tight mt-12 md:text-2xl">
            Register Yourself
          </h1>

          <form
            className="mt-6"
            action=""
            method="POST"
            onSubmit={handleSubmit}
          >
            <div>
              <label htmlFor="" className="block text-gray-700">
                First Name
              </label>
              <input
                type="name"
                placeholder="First name"
                className="w-full bg-gray-200 mt-2 border rounded-lg px-4 py-3 focus:border-blue-500 focus:bg-white focus:outline-none"
                autoComplete="off"
                autoFocus
                // required
                name="firstname"
                value={inputValue.firstname}
                onChange={handleChange}
              />
            </div>
            
            <p className="text-sm text-red-500 px-1 py-1">{errors.firstname}</p>

            <div className="mt-4">
              <label htmlFor="" className="block text-gray-700">
                Last Name
              </label>
              <input
                type="name"
                placeholder="Last name"
                className="w-full bg-gray-200 mt-2 border rounded-lg px-4 py-3 focus:border-blue-500 focus:bg-white focus:outline-none"
                autoComplete="off"
                autoFocus
                // required
                name="lastname"
                value={inputValue.lastname}
                onChange={handleChange}
              />
            </div>
            
            <p className="text-sm text-red-500 px-1 py-1">{errors.lastname}</p>
            

            <div className="mt-4">
              <label htmlFor="" className="block text-gray-700">
                Email Address
              </label>
              <input
                type="email"
                placeholder="kamran@example.com"
                className="w-full bg-gray-200 mt-2 border rounded-lg px-4 py-3 focus:border-blue-500 focus:bg-white focus:outline-none"
                autoComplete="off"
                autoFocus
                // required
                name="email"
                value={inputValue.email}
                onChange={handleChange}
              />
            </div>
            
            <p className="text-sm text-red-500 px-1 py-1">{errors.email}</p>
            

            <div className="mt-4">
              <label htmlFor="" className="block text-gray-700">
                Password
              </label>
              <input
                type="password"
                placeholder="Enter password"
                className="w-full bg-gray-200 mt-2 border rounded-lg px-4 py-3 focus:border-blue-500 focus:bg-white focus:outline-none"
                autoComplete="off"
                autoFocus
                // required
                name="password"
                value={inputValue.password}
                onChange={handleChange}
              />
            </div>
            <p className="text-sm text-red-500 px-1 py-1">{errors.password}</p>
            

            <div className="mt-4">
              <label htmlFor="" className="block text-gray-700">
                Confirm Password
              </label>
              <input
                type="password"
                placeholder="Confirm Password"
                className="w-full bg-gray-200 mt-2 border rounded-lg px-4 py-3 focus:border-blue-500 focus:bg-white focus:outline-none"
                autoComplete="off"
                autoFocus
                // required
                name="confirmpassword"
                value={inputValue.confirmpassword}
                onChange={handleChange}
              />
            </div>
            
            <p className="text-sm text-red-500 px-1 py-1">{errors.password}</p>
            
            
            

            <div className="text-right mt-2">
              <a
                href=" "
                className="text-sm font-semibold text-gray-700 hover:text-blue-700 focus:text-blue-700 focus:outline-none"
              >
                Forget Password ?
              </a>
            </div>

            <button
              type="submit"
              className="w-full block bg-blue-500 hover:bg-blue-400 px-4 py-3 mt-6 rounded-lg font-semibold text-white focus:bg-blue-400 focus:outline-none"
      
            >
              Sign-up
            </button>

            <hr className="my-6 border-gray-300 w-full" />

            <button
              type="button"
              className="w-full block bg-white border-gray-300 hover:bg-gray-100 focus:bg-gray-100 text-gray-900 font-semibold rounded-lg px-4 py-3 border  "
            >
              <div className="flex items-center justify-center">
                <i className="fa-google fa-brands "></i>
                <span className="ml-4">Log in with Google</span>
              </div>
            </button>

            <p className="text-sm text-gray-500 mt-12 pb-1">
              &copy; 2022 Kamran Mughals - All Rights Reserved
            </p>
          </form>
        </div>
      </div>
    </section>
  
           {/* <form action="" method="POST" onSubmit={handleSubmit}>
        <div>
          <label htmlFor="FirstName">First Name</label>
          <input
            type="name"
            name="firstname"
            value={inputValue.firstname}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="FirstName">last Name</label>
          <input
            type="name"
            name="lastname"
            value={inputValue.lastname}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="email">E-mail Address</label>
          <input
            type="email"
            name="email"
            autoComplete="off"
            value={inputValue.email}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            name="password"
            value={inputValue.password}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="confirmpassword">Confirm Password</label>
          <input
            type="password"
            name="confirmpassword"
            value={inputValue.confirmpassword}
            onChange={handleChange}
          />
        </div>
        <div>
          <button type="submit" onClick={PostData}>
            Register
          </button>
        </div>
      </form> */}
    </> 
  )
};

export default Register;
